import React, {  useRef, useState } from "react";
import { useDispatch } from "react-redux";
import { createTodo } from "../../store/actions";
import { InputWithHighlight } from '../index';

const CreateTodo = () => {
  const dispatch = useDispatch();
  const inputRef = useRef();
  const [text, setText] = useState("");

  const dispatchCreateTodo = () => {
    const regExp = /#[0-9A-Za-zА-Яа-яё]+/g;

    dispatch(createTodo({ text }));
  };

  const changeText = () => {
    setText(inputRef.current.value);
  };

  return (
    <>
      <InputWithHighlight onChange={changeText} text={text} inputRef={inputRef} />
      <button disabled={text.length === 0} onClick={dispatchCreateTodo}>Create</button>
    </>
  );
};

export default CreateTodo;
