import styled from "styled-components";

export const TagContainer = styled.div`
  display: flex;
  min-height: 50px;
  background-color: black;
  color: white;
  min-width: 70px;
  align-items: center;
  border-radius: 5px;
  justify-content: center;
  :hover{
    transform: scale(1.05,1.05);
    transition: 0.4s ease-in-out ;
  }
`;
